
public class Result10 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int j=1;
		int result=0;
		int i;
		for(i=1;i<=10;i++)
		{
			j=j*i;
			result+=j;
			System.out.println("result="+result);
		}
		System.out.println("result="+result);
	}

}
